require 'spec_helper'

describe WebconfServersController do
  include Shoulda::Matchers::ActionController

  # standard routes generated by bigbluebutton_rails
  describe "routing" do
    it { should route(:get, "/webconf/servers").to(:action => :index) }
    it { should route(:post, "/webconf/servers").to(:action => :create) }
    it { should route(:get, "/webconf/servers/new").to(:action => :new) }
    it { should route(:get, "/webconf/servers/server-1/edit").to(:action => :edit, :id => "server-1") }
    it { should route(:get, "/webconf/servers/server-1").to(:action => :show, :id => "server-1") }
    it { should route(:put, "/webconf/servers/server-1").to(:action => :update, :id => "server-1") }
    it { should route(:delete, "/webconf/servers/server-1").to(:action => :destroy, :id => "server-1") }
    it { should route(:get, "/webconf/servers/server-1/activity").to(:action => :activity, :id => "server-1") }
  end

end
